pragma solidity 0.8.6;

import '@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol';
import './BaseRelayRecipient.sol';

contract AccessControl is OwnableUpgradeable, BaseRelayRecipient {
	mapping(address => bool) private _admins;
	event AdminAccessSet(address admin, bool enabled);

	function setAdminAccess(address admin, bool enabled) public onlyOwner {
		_admins[admin] = enabled;
		emit AdminAccessSet(admin, enabled);
	}

	function isAdmin(address admin) public view returns (bool) {
		return _admins[admin] || admin == owner();
	}

	modifier onlyAdmin() {
		require(isAdmin(_msgSender()), 'Caller is not admin');
		_;
	}

	/**
	 * return the sender of this call.
	 * if the call came through our trusted forwarder, return the original sender.
	 * otherwise, return `msg.sender`.
	 * should be used in the contract anywhere instead of msg.sender
	 */
	function _msgSender()
		internal
		view
		virtual
		override(BaseRelayRecipient, ContextUpgradeable)
		returns (address ret)
	{
		return BaseRelayRecipient._msgSender();
	}
}
","keccak256":"0x287ea1f752006caa987f06affab146a9d6fde00a5bcb861b57d2889b2019a6e8","license":"MIT"},"src/BaseRelayRecipient.sol":{"content":"// SPDX-License-Identifier:MIT
pragma solidity ^0.8.6;

/**
 * A base contract to be inherited by any contract that want to receive relayed transactions
 * A subclass must use "_msgSender()" instead of "msg.sender"
 */
abstract contract BaseRelayRecipient {
	/*
	 * Forwarder singleton we accept calls from
	 */
	address public trustedForwarder;

	/*
	 * require a function to be called through GSN only
	 */
	modifier trustedForwarderOnly() {
		require(
			msg.sender == address(trustedForwarder),
			'Function can only be called through the trusted Forwarder'
		);
		_;
	}

	function isTrustedForwarder(address forwarder) public view returns (bool) {
		return forwarder == trustedForwarder;
	}

	/**
	 * return the sender of this call.
	 * if the call came through our trusted forwarder, return the original sender.
	 * otherwise, return `msg.sender`.
	 * should be used in the contract anywhere instead of msg.sender
	 */
	function _msgSender() internal view virtual returns (address ret) {
		if (msg.data.length >= 24 && isTrustedForwarder(msg.sender)) {
			// At this point we know that the sender is a trusted forwarder,
			// so we trust that the last bytes of msg.data are the verified sender address.
			// extract sender address from the end of msg.data
			assembly {
				ret := shr(96, calldataload(sub(calldatasize(), 20)))
			}
		} else {
			return msg.sender;
		}
	}
}
